import React, { useState } from "react";
import SystemContextProvider, { SystemContext } from "../Context/SystemContext";
import './CSS/LoginSignup.css';

const LoginSignup = () => {
    const [state, setState] = useState("Login");
    const [formData, setFormData] = useState({
        username: "",
        password: "",
        email: ""
    });

    const changeHandler = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }

    const login = async () => {
      console.log("Login exec", formData);
      let responseData;
      await fetch('http://localhost:4000/login', {
          method: 'POST',
          headers: {
              Accept: 'application/form-data',
              'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
      })
          .then((response) => response.json())
          .then((data) => responseData = data);

      if (responseData.success) {
          console.log("Login success:", formData.email);
          localStorage.setItem('auth-token', responseData.token);
          localStorage.setItem('user-email', formData.email); 
          window.location.replace("/");
      } else {
          alert(responseData.error);
      }
  }

  const signup = async () => {
    console.log("Signup exec", formData);
    let responseData;
    await fetch('http://localhost:4000/signup', {
        method: 'POST',
        headers: {
            Accept: 'application/form-data',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
    })
        .then((response) => response.json())
        .then((data) => responseData = data);
    
    if (responseData.success) {
        console.log("Signup success:", formData.email);
        localStorage.setItem('auth-token', responseData.token);
        localStorage.setItem('user-email', formData.email);
        window.location.replace("/");
    } else {
        alert(responseData.error);
    }
}

  return (
    <div className='loginsignup'>
            <div className="loginsignup-container">
                <h1>{state}</h1>
                <div className="loginsignup-fields">
                    {state === "Sign Up" ? <input name='username' value={formData.username} onChange={changeHandler} type="text" placeholder="Name" /> : null}
                    <input name='email' value={formData.email} onChange={changeHandler} type="email" placeholder="Email Address" />
                    <input name='password' value={formData.password} onChange={changeHandler} type="password" placeholder="Password" />
                </div>
                <button onClick={() => { state === "Login" ? login() : signup() }}>Continue</button>
                {state === "Sign Up" ? <p className="loginsignup-login">Already have an account? <span onClick={() => { setState("Login") }}>Login here</span></p>
                    : <p className="loginsignup-login">Create an account? <span onClick={() => { setState("Sign Up") }}>Click here</span></p>}
            </div>
        </div>
  )
}

export default LoginSignup;
