import React, { useState } from "react";
import SystemContextProvider, { SystemContext } from "../Context/SystemContext";
import './CSS/LoginSignup.css';

const ApplicationForm = () => {
    const [formData, setFormData] = useState({
        firstName: "",
        lastEmail: "",
        email: "",
        phone: "",
        university: "",
        program: "",
        startDate: "",
        endDate: "",
        motivationLetter: ""
    });

    const changeHandler = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }

    const apply = async () => {
      console.log("Login exec", formData);
      let responseData;
      await fetch('http://localhost:4000/addapplication', {
          method: 'POST',
          headers: {
              Accept: 'application/form-data',
              'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
      })
          .then((response) => response.json())
          .then((data) => responseData = data);

      if (responseData.success) {
          console.log("Application Form Adding success:", formData.email);
          window.location.replace("/");
      } else {
          alert(responseData.error);
      }
  }

  return (
    <div className='loginsignup'>
            <div className="loginsignup-container">
                <h1>{state}</h1>
                <div className="loginsignup-fields">
                    <input name='firstname' value={formData.username} onChange={changeHandler} type="text" placeholder="Name" />
                    <input name='email' value={formData.email} onChange={changeHandler} type="email" placeholder="Email Address" />
                    <input name='password' value={formData.password} onChange={changeHandler} type="password" placeholder="Password" />
                </div>
                <button onClick={() => { apply() }}>Add Applyvation Form</button>
            </div>
        </div>
  )
}

export default ApplicationForm;
