import React, { useContext } from "react";
import './ProfilePrograms.css';
import { SystemContext } from "../../Context/SystemContext";

// Client-side: Display profile data in ProfilePrograms
const ProfilePrograms = () => {
    const { profileData } = useContext(SystemContext);

     // Перевірка на наявність userData і applications
     if (!profileData.userData || !profileData.applications) {
        return <p>Loading...</p>; // Або будь-який інший індикатор завантаження
    }


    return (
        <div className="profile-details">
            <h2>User Profile</h2>
            <p><strong>First Name:</strong> {profileData.userData.firstName}</p>
            <p><strong>Last Name:</strong> {profileData.userData.lastName}</p>
            <p><strong>Email:</strong> {profileData.userData.email}</p>
            <h2>Application Forms</h2>
            <table className="profile-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Program</th>
                        <th>University</th>
                        <th>Status</th>
                        <th>Start Date</th>
                        <th>End Date</th>
                    </tr>
                </thead>
                <tbody>
                    {profileData.applications.map((app) => (
                        <tr key={app.id}>
                            <td>{app.id}</td>
                            <td>{app.program}</td>
                            <td>{app.university}</td>
                            <td>{app.status}</td>
                            <td>{new Date(app.startDate).toLocaleDateString()}</td>
                            <td>{new Date(app.endDate).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ProfilePrograms;
