import React, { useContext } from "react";
import './ProfilePrograms.css';
import { SystemContext } from "../../Context/SystemContext";
import remove_icon from '../Assets/cart_cross_icon.png';

// Client-side: Display profile data and programs in ProfilePrograms
const ProfilePrograms = () => {
    const { all_program, profilePrograms } = useContext(SystemContext);

    return (
        <div className="cartitems">
            <h2>User Programs</h2>
            <div className="cartitems-format-main">
                <p>Programs</p>
                <p>Title</p>
                <p>Application Status</p>
            </div>
            <hr />
            {all_program.map((e) => {
                if (profilePrograms[e.id] > 0) {
                    return (
                        <div key={e.id}>
                            <div className="cartitems-format cartitems-format-main">
                                <img src={e.image} alt="" className='cartitems-product-icon' />
                                <p>{e.name}</p>
                                <p>{e.status}</p>
                            </div>
                            <hr />
                        </div>
                    );
                }
                return null;
            })}
        </div>
    );
};

export default ProfilePrograms;
