import React, { useState, useEffect } from "react";
import './Hero.css';
import slide1 from '../Assets/slide1.webp'
import slide2 from '../Assets/slide2.webp'
import slide3 from '../Assets/slide3.webp'

const slides = [
    {
        image: slide1,
        title: 'Explore the New Collection',
        description: 'Discover our latest arrivals for everyone.'
    },
    {
        image: slide2,
        title: 'Timeless Fashion',
        description: 'Style that lasts through every season.'
    },
    {
        image: slide3,
        title: 'Modern Designs',
        description: 'Elegant and contemporary styles for every occasion.'
    }
];

const Hero = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentSlide((prev) => (prev + 1) % slides.length);
        }, 5000);
        return () => clearInterval(interval);
    }, []);

    return (
        <div className='hero'>
            <div className="hero-slide" style={{ backgroundImage: `url(${slides[currentSlide].image})` }}>
                <div className="hero-content">
                    <h2>{slides[currentSlide].title}</h2>
                    <p>{slides[currentSlide].description}</p>
                </div>
            </div>
        </div>
    );
};

export default Hero;
