import React, { useContext, useRef, useState } from 'react'
import './Navbar.css'
import logo from '../Assets/logo.png'
import cart_icon from '../Assets/cart_icon.png'
import { Link } from 'react-router-dom'
import { SystemContext } from '../../Context/SystemContext'
import nav_dropdown from '../Assets/dropdown_icon.png'

const Navbar = () => {

    const [menu, setMenu] = useState("home")
    const menuRef = useRef();
    const { updateUserEmail } = useContext(SystemContext);
    
    const dropdown_toogle = (e) => {
        menuRef.current.classList.toggle('nav-menu-visible');
        e.target.classList.toggle('open');
    }

  return (
    <div className='navbar'>
            <div className='nav-logo'>
                <img src={logo} alt="" />
                <p>Academic Mobility System</p>
            </div>
            <img className='nav-dropdown' onClick={dropdown_toogle} src={nav_dropdown} alt="" />
            <ul ref={menuRef} className="nav-menu">
                <li onClick={()=>{setMenu("home")}}><Link style={{ textDecoration: 'none' }} to='/'>Home</Link>{menu==="home"?<hr/>:<></>}</li>
                <li onClick={()=>{setMenu("programs")}}><Link style={{ textDecoration: 'none' }} to='/programs'>Programs</Link>{menu==="programs"?<hr/>:<></>}</li>
            </ul>
            <div className="nav-login-cart">
                {localStorage.getItem('auth-token') ? (
                    <>
                    <button onClick={() => {
                        localStorage.removeItem('auth-token');
                        window.location.replace("/");
                    }}>Log out
                    </button>
                    <Link to='/profile'><img src={cart_icon} alt="Profile" /></Link>
                    </>
                ) : (
                    <Link to='/login'><button>Login</button></Link>
                )}
            </div>
    </div>
  )
}

export default Navbar
