import React, { useContext, useEffect, useState } from "react";
import './ProfilePrograms.css';
import { SystemContext } from "../../Context/SystemContext";

// Client-side: Display profile data in ProfilePrograms
const ProfilePrograms = () => {
    const { profileData, applications } = useContext(SystemContext); // Додано applications з контексту
    const [programImages, setProgramImages] = useState({}); // Стан для збереження картинок програм

    useEffect(() => {
        const fetchProgramImages = async () => {
            const images = {};
            for (const app of applications) {
                if (!images[app.program]) {
                    try {
                        const response = await fetch(`http://localhost:4000/getprogram/${app.program}`);
                        const data = await response.json();
                        if (data.success) {
                            images[app.program] = data.programImage; // Зберігаємо картинку за ім'ям програми
                        }
                    } catch (error) {
                        console.error("Error fetching program image:", error);
                    }
                }
            }
            setProgramImages(images);
        };

        if (applications.length > 0) {
            fetchProgramImages();
        }
    }, [applications]);

    return (
        <div className="profile-details">
            <h2>User Profile</h2>
            <p><strong>First Name:</strong> {profileData.firstName}</p>
            <p><strong>Last Name:</strong> {profileData.lastName}</p>
            <p><strong>Email:</strong> {profileData.email}</p>
            <h2>Application Forms</h2>
            <table className="profile-table">
                <thead>
                    <tr>
                        <th>Program</th>
                        <th>Motivational Letter</th>
                        <th>University</th>
                        <th>Status</th>
                        <th>Start Date</th>
                        <th>End Date</th>
                    </tr>
                </thead>
                <tbody>
                    {applications && applications.length > 0 ? (
                        applications.map((app) => (
                            <tr key={app.id}>
                                <td>
                                    <div className="program-info">
                                        {programImages[app.program] && (
                                            <img src={programImages[app.program]} alt={app.program} className="program-image" />
                                        )}
                                        {app.program}
                                    </div>
                                </td>
                                <td>{app.motivationalLetter}</td>
                                <td>{app.university}</td>
                                <td>{app.status}</td>
                                <td>{new Date(app.startDate).toLocaleDateString()}</td>
                                <td>{new Date(app.endDate).toLocaleDateString()}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6">No applications found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ProfilePrograms;
