import React, { useContext } from "react";
import "./ProgramDisplay.css";
import { SystemContext } from "../../Context/SystemContext";

const ProgramDisplay = (props) => {

    const { getTotalCartAmount, all_product, cartItems, removeFromCart, addOrder, userEmail } = useContext(ShopContext);

    const { program } = props;

  return (
    <div className="productdisplay">
            <div className="productdisplay-left">
                <div className="productdisplay-img">
                    <img
                        className="productdisplay-main-img"
                        src={program.image}
                        alt=""
                    />
                </div>
            </div>
            <div className="productdisplay-right">
                <h1>{program.name}</h1>
                <div className="productdisplay-right-description">
                    {program.description}
                </div>
                <button onClick={() => {window.location.replace(`/app-form/${program.id}`)}}>
                    SEND APPLICATION FORM
                </button>
            </div>
        </div>
  );
};

export default ProgramDisplay;
