import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import './CSS/ApplicationForm.css';

const ApplicationForm = () => {
    const { programId } = useParams();
    const [file, setFile] = useState(null);
    const [previewFile, setPreviewFile] = useState(null);
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        email: "",
        phone: "",
        university: "",
        program: "",
        startDate: "",
        endDate: "",
        motivationLetter: "",
        status: "Pending"
    });

    const fileHandler = async (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
        setPreviewFile(selectedFile.name);

        let responseData;
        let formDataUpload = new FormData();
        formDataUpload.append('letter', selectedFile);

        await fetch('http://localhost:4000/uploadletter', {
            method: 'POST',
            headers: {
                Accept: 'application/json'
            },
            body: formDataUpload,
        })
        .then((resp) => resp.json())
        .then((data) => responseData = data);

        if (responseData.success) {
            setFormData((prev) => ({ ...prev, motivationLetter: responseData.letter_url }));
        } else {
            alert("Failed to upload letter");
        }
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const year = date.getFullYear();
        return `${day}-${month}-${year}`;
    };

    useEffect(() => {
        // Завантаження профілю
        if (localStorage.getItem('auth-token')) {
            fetch('http://localhost:4000/getprofile', {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'auth-token': `${localStorage.getItem('auth-token')}`,
                    'Content-Type': 'application/json',
                },
            })
            .then((response) => response.json())
            .then((data) => {
                setFormData((prev) => ({
                    ...prev,
                    firstName: data.firstName,
                    lastName: data.lastName,
                    email: data.email
                }));
            });
        }

        // Завантаження програми
        fetch(`http://localhost:4000/getprogramId/${programId}`)
            .then((response) => response.json())
            .then((data) => {
                if (data.success) {
                    setFormData((prev) => ({
                        ...prev,
                        program: data.programName,
                        startDate: formatDate(data.programStartDate),
                        endDate: formatDate(data.programEndDate)
                    }));
                }
            });
    }, [programId]);

    const apply = async () => {
        let responseData;
        await fetch('http://localhost:4000/addapplication', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
        .then((response) => response.json())
        .then((data) => responseData = data);

        if (responseData.success) {
            window.location.replace("/");
        } else {
            alert(responseData.error);
        }
    };

    return (
        <div className="application-form">
            <div className="application-form-container">
                <h1 className="application-form-title">Application Form</h1>
                <div className="application-form-fields">
                    <input name="firstName" value={formData.firstName} type="text" readOnly />
                    <input name="startDate" value={formData.startDate} type="text" readOnly />
                    <input name="endDate" value={formData.endDate} type="text" readOnly />
                </div>
                <button className="application-form-button" onClick={apply}>
                    Submit Application
                </button>
            </div>
        </div>
    );
};

export default ApplicationForm;
