import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import './CSS/LoginSignup.css';
import upload_area from '../Components/Assets/upload_area.svg';

const ApplicationForm = () => {
    const { programId } = useParams();
    const [file, setFile] = useState(null);
    const [previewFile, setPreviewFile] = useState(null);
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        email: "",
        phone: "",
        university: "",
        program: "",
        startDate: "",
        endDate: "",
        motivationLetter: "",
        status: "New Application"
    });

    const fileHandler = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
        setPreviewFile(URL.createObjectURL(selectedFile));
    };

    useEffect(() => {
        // Fetch user profile data
        if (localStorage.getItem('auth-token')) {
            fetch('http://localhost:4000/getprofile', {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'auth-token': `${localStorage.getItem('auth-token')}`,
                    'Content-Type': 'application/json',
                },
            })
                .then((response) => response.json())
                .then((data) => {
                    setFormData((prev) => ({
                        ...prev,
                        firstName: data.firstName,
                        lastName: data.lastName,
                        email: data.email,
                    }));
                })
                .catch((error) => console.error("Error fetching user data:", error));
        }

        // Fetch program name
        const fetchProgramName = async () => {
            try {
                const response = await fetch(`http://localhost:4000/getprogramId/${programId}`);
                const data = await response.json();
                if (data.success) {
                    setFormData((prev) => ({ ...prev, program: data.programName }));
                } else {
                    alert("Program not found");
                }
            } catch (error) {
                console.error("Error fetching program data:", error);
            }
        };

        if (programId) {
            fetchProgramName();
        }
    }, [programId]);

    const changeHandler = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    }

    const uploadMotivationLetter = async () => {
        let responseData;
        let formDataUpload = new FormData();
        formDataUpload.append('letter', file);

        await fetch('http://localhost:4000/uploadletter', {
            method: 'POST',
            headers: {
                Accept: 'application/json'
            },
            body: formDataUpload,
        })
        .then((resp) => resp.json())
        .then((data) => responseData = data);

        if (responseData.success) {
            setFormData({ ...formData, motivationLetter: responseData.letter_url });
        } else {
            alert("Failed to upload letter");
        }
    };

    const apply = async () => {
        console.log("Apply exec", formData);
        let responseData;
        await fetch('http://localhost:4000/addapplication', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then((response) => response.json())
            .then((data) => responseData = data);

        if (responseData.success) {
            console.log("Application Form Adding success:", formData.email);
            window.location.replace("/");
        } else {
            alert(responseData.error);
        }
    }

    return (
        <div className='loginsignup'>
            <div className="loginsignup-container">
                <div className="loginsignup-fields">
                    <input name='firstName' value={formData.firstName} onChange={changeHandler} type="text" readOnly />
                    <input name='lastName' value={formData.lastName} onChange={changeHandler} type="text" readOnly />
                    <input name='email' value={formData.email} onChange={changeHandler} type="email" readOnly />
                    <input name='phone' value={formData.phone} onChange={changeHandler} type="text" placeholder="Phone" />
                    <input name='university' value={formData.university} onChange={changeHandler} type="text" placeholder="University" />
                    <input name='program' value={formData.program} type="text" placeholder="Program" readOnly />
                    <input name='startDate' value={formData.startDate} onChange={changeHandler} type="text" placeholder="Start Date" />
                    <input name='endDate' value={formData.endDate} onChange={changeHandler} type="text" placeholder="End Date" />
                    <input name='motivationLetter' value={formData.motivationLetter} type="text" placeholder="Motivation Letter URL" readOnly />
                    <label htmlFor="file-input">
                        <img src={previewFile || upload_area} className='addproduct-thumnail-img' alt="" />
                    </label>
                    <input type="file" onChange={fileHandler} id='file-input' hidden />
                    <button onClick={uploadMotivationLetter}>Upload Motivation Letter</button>
                </div>
                <button onClick={() => { apply() }}>Add Application Form</button>
            </div>
        </div>
    )
}

export default ApplicationForm;
